import { ArrowRight } from "lucide-react";

type Content = {
  title: string;
  webinars: {
    id: string;
    title: string;
    summary: string;
    label: string;
    author: string;
    published: string;
    href: string;
    image: string;
  }[];
};

const content: Content = {
  title: "Discover Our Design System",
  webinars: [
    {
      id: "webinar-1",
      title: "Introduction to Buildy/UI: A Modern Design System",
      summary:
        "Learn how Buildy/UI empowers developers and designers to create efficient, accessible, and beautiful interfaces effortlessly.",
      label: "UI/UX Insights",
      author: "Alex Dev",
      published: "10 Feb 2024",
      href: "#",
      image: "https://placehold.co/1280x740?text=UI/UX+Insights",
    },
    {
      id: "webinar-2",
      title: "Advanced Prototyping with Buildy/UI",
      summary:
        "Explore advanced techniques for rapid prototyping and collaboration using our modular component library.",
      label: "Prototyping Techniques",
      author: "Jordan Smith",
      published: "15 Feb 2024",
      href: "#",
      image: "https://placehold.co/1280x740?text=Prototyping+Techniques",
    },
  ],
} as const;

type DesignSystemWebinarsProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const DesignSystemWebinars = (props: DesignSystemWebinarsProps) => {
  const { title, webinars } = {
    ...content,
    ...props
  };

  return (
  <section className="w-full py-16 lg:py-32">
    <div className="container mx-auto px-4 md:px-6 lg:px-8 flex flex-col gap-12">
      {/* Section header */}
      <header className="text-center py-8 lg:py-16">
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold">
          {title}
        </h2>
      </header>
      {/* Webinars grid */}
      <div className="grid gap-8 sm:grid-cols-12 lg:gap-10">
        {webinars?.map((webinar) => (
          <a
            key={webinar.id}
            href={webinar.href}
            className="group grid gap-y-4 sm:grid-cols-10 sm:gap-y-0 sm:gap-x-6 lg:gap-x-8 sm:col-span-12"
          >
            {/* Webinar content vertically centered */}
            <div className="sm:col-span-5 flex flex-col gap-4 justify-center">
              <div className="text-xs uppercase tracking-wider text-muted-foreground flex gap-4">
                <span>{webinar.label}</span>
                <span>{webinar.author}</span>
              </div>
              <h3 className="text-xl md:text-2xl lg:text-3xl font-semibold">
                {webinar.title}
              </h3>
              <p className="text-sm text-muted-foreground">{webinar.summary}</p>
              <div className="flex items-center gap-2 text-primary font-medium">
                <span>Read more</span>
                <ArrowRight className="w-4 h-4 transition-transform group-hover:translate-x-1" />
              </div>
            </div>
            {/* Webinar image */}
            <div className="sm:col-span-5">
              <div className="aspect-video overflow-hidden rounded-lg border border-border">
                <img
                  src={webinar.image}
                  alt={webinar.title}
                  className="object-cover transition-transform group-hover:scale-105"
                />
              </div>
            </div>
          </a>
        ))}
        </div>
      </div>
    </section>
  );
};

import { ArrowRight } from "lucide-react";
import { Button, type ButtonProps } from "@/components/ui/button";

type Content = {
  tagline: string;
  title: string;
  description: string;
  button: ButtonProps & {
    name: string;
    icon: React.ReactNode;
  };
  posts: {
    id: string;
    title: string;
    summary: string;
    label: string;
    author: string;
    published: string;
    href: string;
    image: string;
  }[];
};

const content: Content = {
  tagline: "Explore Insights",
  title: "Latest Blog Posts",
  description:
    "Stay updated with the latest trends, tips, and best practices in UI/UX design. Explore how Buildy/UI empowers developers and businesses alike.",
  button: {
    name: "All Posts",
    variant: "link",
    icon: <ArrowRight className="w-4 h-4" />,
  },
  posts: [
    {
      id: "post-1",
      title: "The Power of Atomic Design in Modern UI/UX",
      summary:
        "Discover how atomic design principles can help you create consistent and scalable user interfaces for any application.",
      label: "Design Systems",
      author: "Jane Doe",
      published: "1 Jan 2024",
      href: "#",
      image: "https://placehold.co/600x400?text=Atomic+Design",
    },
    {
      id: "post-2",
      title: "Why Tailwind CSS is a Game Changer for Developers",
      summary:
        "Learn how utility-first CSS frameworks like Tailwind CSS streamline development without compromising on design.",
      label: "CSS Frameworks",
      author: "John Smith",
      published: "15 Jan 2024",
      href: "#",
      image: "https://placehold.co/600x400?text=Tailwind+CSS",
    },
    {
      id: "post-3",
      title: "Building Accessible Components with shadcn/ui",
      summary:
        "Explore the accessibility-first approach of shadcn/ui and how it ensures inclusivity for all users.",
      label: "Accessibility",
      author: "Alex Johnson",
      published: "22 Jan 2024",
      href: "#",
      image: "https://placehold.co/600x400?text=shadcn/ui",
    },
  ],
} as const;

type BlogPostsGridSectionProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>; 

export const BlogPostsGridSection = (props: BlogPostsGridSectionProps) => {
  const { tagline, title, description, button, posts } = {
    ...content,
    ...props
  };

  return (
  <section className="w-full py-16 lg:py-32">
    <div className="container mx-auto px-4 md:px-6 lg:px-8 flex flex-col gap-16 text-center">
      <header className="max-w-2xl mx-auto flex flex-col gap-4">
        <p className="text-xs font-medium uppercase tracking-widest text-muted-foreground">
          {tagline}
        </p>
        <h2 className="text-3xl font-bold md:text-4xl lg:text-5xl">
          {title}
        </h2>
        <p className="text-muted-foreground lg:text-lg">
          {description}
        </p>
        <Button variant={button.variant} className="gap-2 self-center">
          {button.name} {button.icon}
        </Button>
      </header>
      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
        {posts?.map((post) => (
          <a
            key={post.id}
            href={post.href}
            className="flex flex-col rounded-lg border border-border overflow-hidden"
          >
            <img
              src={post.image}
              alt={post.title}
              className="aspect-video object-cover"
            />
            <div className="flex flex-col gap-4 p-6 text-start">
              <h3 className="text-lg font-semibold lg:text-xl">
                {post.title}
              </h3>
              <p className="text-sm text-muted-foreground">
                {post.summary}
              </p>
              <p className="flex items-center text-sm font-medium text-primary hover:underline">
                Read more <ArrowRight className="ml-2 w-4 h-4" />
              </p>
            </div>
          </a>
        ))}
        </div>
      </div>
    </section>
  );
};

import { MoveRight } from "lucide-react";
import { Button, type ButtonProps } from "@/components/ui/button";

type Content = {
  title: string;
  button?: (ButtonProps & {
    text?: string;
    icon?: React.ReactNode;
  });
  articles: {
    id: string;
    title: string;
    description: string;
    imageAlt: string;
  }[];
};

const content: Content = {
  title: "Latest articles",
  button: {
    text: "View all articles",
    icon: <MoveRight className="w-4 h-4" />
  },
  articles: [
    {
      id: "1",
      title: "Exploring the Future of Web Design",
      description: "Discover how emerging technologies are reshaping the digital landscape.",
      imageAlt: "Future of Web Design"
    },
    {
      id: "2",
      title: "10 Essential UI Design Trends for 2024",
      description: "Stay ahead of the curve with these cutting-edge design principles.",
      imageAlt: "UI Design Trends"
    },
    {
      id: "3",
      title: "Mastering React: Advanced Techniques for Building Scalable Apps",
      description: "Learn how to optimize your React applications for better performance and maintainability.",
      imageAlt: "React Advanced Techniques"
    }
  ]
} as const;

type BlogCardsSectionProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;  

export const BlogCardsSection = (props: BlogCardsSectionProps) => {
  const { title, articles, button } = {
    ...content,
    ...props
  };

  return (
  <section className="w-full py-16 lg:py-32">
    <div className="container mx-auto px-4 md:px-6 lg:px-8 flex flex-col gap-8">
      <header className="flex w-full flex-col sm:flex-row sm:justify-between sm:items-center gap-4">
        <h2 className="text-3xl md:text-4xl lg:text-6xl font-bold">
          {title}
        </h2>
        {button && (
          <Button className="gap-4">
            {button.text} {button.icon}
          </Button>
        )}
      </header>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {articles?.map((article) => (
          <article id={article.id} key={article.id} className="flex flex-col gap-4 hover:opacity-75 cursor-pointer">
            <div 
              className="bg-muted rounded-md aspect-video mb-4"
              role="img"
              aria-label={article.imageAlt}></div>
            <h3 className="text-xl tracking-tight">{article.title}</h3>
            <p className="text-muted-foreground text-base">
              {article.description}
            </p>
          </article>
        ))}
        </div>
      </div>
    </section>
  );
};

import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";

type Content = {
  title: string;
  description: string;
  articles: {
    id: string;
    category: string;
    author: {
      name: string;
      avatar: string;
    };
    headline: string;
    description: string;
    className: string;
  }[];
};

const content: Content = {
  title: "Latest Articles",
  description:
    "Exploring the Future of Web Design Trends.",
  articles: [
    {
      id: "article1",
      category: "News",
      author: {
        name: "John Johnsen",
        avatar: "https://github.com/shadcn.png",
      },
      headline: "Streamline Your Business with Buildy/UI",
      description:
        "Simplify your operations and improve productivity with modern tools designed for SMBs.",
      className: "md:col-span-2",
    },
    {
      id: "article2",
      category: "News",
      author: {
        name: "John Johnsen",
        avatar: "https://github.com/shadcn.png",
      },
      headline: "Enhance Supplier Management",
      description:
        "Discover efficient methods to manage supplier relationships and invoices.",
      className: "",
    },
    {
      id: "article3",
      category: "News",
      author: {
        name: "John Johnsen",
        avatar: "https://github.com/shadcn.png",
      },
      headline: "Boost Your Marketing with Buildy/UI",
      description:
        "Learn how to leverage our design system to create impactful marketing strategies.",
      className: "",
    },
  ],
} as const;

type BlogArticlesColumnsCardsProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const BlogArticlesColumnsCards = (props: BlogArticlesColumnsCardsProps) => {
  const { title, description, articles } = {
    ...content,
    ...props
  };

  return (
  <section className="w-full py-8 lg:py-16">
    <div className="container mx-auto px-4 md:px-6 lg:px-8 flex flex-col gap-8">
      <header className="flex flex-col gap-2">
        <h2 className="text-3xl md:text-4xl lg:text-6xl font-bold max-w-xl">
          {title}
        </h2>
        <p className="text-lg text-muted-foreground max-w-2xl">
          {description}
        </p>
      </header>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {articles?.map((article) => (
          <article
            key={article.id}
            className={`flex flex-col gap-4 hover:opacity-75 cursor-pointer ${article.className}`}
          >
            <div className="bg-muted rounded-md aspect-video"></div>
            <div className="flex flex-row gap-4 items-center">
              <Badge>{article.category}</Badge>
              <p className="flex flex-row gap-2 text-sm items-center">
                <span className="text-muted-foreground">By</span>
                <Avatar className="h-6 w-6">
                  <AvatarImage src={article.author.avatar} />
                  <AvatarFallback>
                    {article.author.name[0] + article.author.name.split(" ")[1][0]}
                  </AvatarFallback>
                </Avatar>
                <span>{article.author.name}</span>
              </p>
            </div>
            <div className="flex flex-col gap-2">
              <h3 className="max-w-3xl text-2xl md:text-4xl tracking-tight">
                {article.headline}
              </h3>
              <p className="max-w-3xl text-muted-foreground text-base">
                {article.description}
              </p>
            </div>
          </article>
        ))}
        </div>
      </div>
    </section>
  );
};
