import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";

type Content = {
  title: string;
  promotitle: string;
  subtitle: string;
  description: string;
  testimonials: {
    image: string;
    name: string;
    userName: string;
    comment: string;
  }[];
};

const content = {
    title: "Why Developers Love",
    promotitle: "Buildy/UI",
    subtitle: "This - Freedom to Create",
    description:
      "Buildy/UI empowers developers with clean, unopinionated components, perfectly suited for marketing and business projects. No unnecessary styles, shadows, or gradients – just pure components ready to adapt to your vision.",
  
    testimonials: [
      {
        image: "https://github.com/shadcn.png",
        name: "Sophia Lee",
        userName: "@sophia_uiux",
        comment:
          "With Buildy/UI, I finally have a design system that doesn't dictate my style. The components are clean, consistent, and free from clutter – a perfect foundation for creativity!",
      },
      {
        image: "https://github.com/shadcn.png",
        name: "Daniel Chen",
        userName: "@daniel_frontend",
        comment:
          "Other libraries forced me to strip out shadows and gradients manually. Buildy/UI gave me pure components, making my work faster and cleaner. I can focus on building, not fixing!",
      },
      {
        image: "https://github.com/shadcn.png",
        name: "Olivia Brown",
        userName: "@olivia_dev",
        comment:
          "The components are lightweight and perfectly structured. It's like prototyping but production-ready. Plus, the integration with Radix UI is a game-changer for accessibility!",
      },
      {
        image: "https://github.com/shadcn.png",
        name: "Ethan Smith",
        userName: "@ethan_code",
        comment:
          "Finally, a free library of components tailored for business and marketing. Buildy/UI components saved me hours of development time and delivered consistent results!",
      },
      {
        image: "https://github.com/shadcn.png",
        name: "Liam Carter",
        userName: "@liam_creative",
        comment:
          "Buildy/UI strips away unnecessary fluff. The components are minimal, versatile, and incredibly easy to customize. It's the freedom every designer and developer needs!",
      },
      {
        image: "https://github.com/shadcn.png",
        name: "Emily Johnson",
        userName: "@emily_builder",
        comment:
          "This library is a breath of fresh air. Simple, consistent, and clean components with zero unnecessary styling. Buildy/UI truly feels like a developer's dream toolkit!",
      },
    ],
  } as const;

type PromoTestimonialsProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const PromoTestimonials = (props: PromoTestimonialsProps) => {
  const { title, promotitle, subtitle, description, testimonials } = {
    ...content,
    ...props
  };

  return (
    <section className="container mx-auto px-4 md:px-6 lg:px-8 py-8 lg:py-16">
      <header className="text-center space-y-4">
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold">
          {title}{" "}
          <span className="bg-gradient-to-b from-sky-500 to-sky-700 text-transparent bg-clip-text">
            {promotitle}
          </span>
        </h2>
        <h3 className="text-muted-foreground text-2xl md:text-3xl lg:text-4xl font-bold py-8">
          {subtitle}
        </h3>
        <p className="text-lg text-muted-foreground">
          {description}
        </p>
      </header>

      <div className="grid md:grid-cols-2 lg:grid-cols-4 sm:block columns-2  lg:columns-3 lg:gap-6 mx-auto space-y-4 lg:space-y-6 mt-16">
        {testimonials?.map(
          ({ image, name, userName, comment }) => (
            <div
              key={name}
              className="relative p-[1px] bg-gradient-to-br from-sky-600 to-sky-300 dark:to-slate-600 rounded-2xl"
            >
              <Card
                className="bg-secondary h-full relativemax-w-md md:break-inside-avoid rounded-2xl"
              >
                <CardHeader className="flex flex-row items-center gap-4 pb-2">
                  <Avatar>
                    <AvatarImage
                      alt=""
                      src={image}
                    />
                    <AvatarFallback>OM</AvatarFallback>
                  </Avatar>

                  <div className="flex flex-col">
                    <CardTitle className="text-lg">{name}</CardTitle>
                    <CardDescription>{userName}</CardDescription>
                  </div>
                </CardHeader>

                <CardContent>{comment}</CardContent>
              </Card>
            </div>
          )
        )}
      </div>
    </section>
  );
};

import { buttonVariants } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Facebook, Instagram, Linkedin } from "lucide-react";

type Content = {
  title: string;
  promotitle: string;
  description: string;
  teamList: {
    imageUrl: string;
    name: string;
    position: string;
    slogan: string;
    socialNetworks: { name: string; url: string }[];
  }[];
};

const content: Content = {
  title: "Meet Our",
  promotitle: "Team",
  description:
    "The Buildy/UI team is dedicated to delivering exceptional UI components with a focus on modern frontend stacks and seamless developer experience.",
  teamList: [
    {
      imageUrl: "https://avatars.githubusercontent.com/u/124599",
      name: "Sophia Lee",
      position: "UI/UX Designer",
      slogan: "Crafting beautiful and functional designs for seamless user experiences.",
      socialNetworks: [
        { name: "Linkedin", url: "#" },
        { name: "Facebook", url: "#" },
        { name: "Instagram", url: "#" },
      ],
    },
    {
      imageUrl: "https://avatars.githubusercontent.com/u/124599",
      name: "Daniel Chen",
      position: "Frontend Developer",
      slogan: "Bringing ideas to life with clean and efficient code.",
      socialNetworks: [
        { name: "Linkedin", url: "#" },
        { name: "Facebook", url: "#" },
        { name: "Instagram", url: "#" },
      ],
    },
    {
      imageUrl: "https://avatars.githubusercontent.com/u/124599",
      name: "Olivia Brown",
      position: "Programmer",
      slogan: "Ensuring performance and scalability in every component.",
      socialNetworks: [
        { name: "Linkedin", url: "#" },
        { name: "Instagram", url: "#" },
      ],
    },
    {
      imageUrl: "https://avatars.githubusercontent.com/u/124599",
      name: "Ethan Smith",
      position: "Illustrator",
      slogan: "Transforming concepts into captivating visuals and graphics.",
      socialNetworks: [
        { name: "Linkedin", url: "#" },
        { name: "Facebook", url: "#" },
      ],
    },
  ] as Content['teamList'],
} as const;

type PromoTeamSectionProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const PromoTeamSection = (props: PromoTeamSectionProps) => {
  const { title, promotitle, description, teamList } = {
    ...content,
    ...props
  };

  const socialIcon = (iconName: string) => {
    switch (iconName) {
      case "Linkedin":
        return <Linkedin size="20" />;
      case "Facebook":
        return <Facebook size="20" />;
      case "Instagram":
        return <Instagram size="20" />;
    }
  };

  return (
    <section className="container mx-auto px-4 md:px-6 lg:px-8 py-16 lg:py-32">
      <header className="text-center space-y-4">
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold">
          {title}{" "}
          <span className="bg-gradient-to-b from-sky-500 to-sky-700 text-transparent bg-clip-text">
          {promotitle}
          </span>
        </h2>
        <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
          {description}
        </p>
      </header>

      <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8 mt-24">
        {teamList?.map(({ imageUrl, name, position, slogan, socialNetworks }) => (
          <div
            key={name}
            className="relative p-[1px] bg-gradient-to-br from-sky-600 to-sky-300 dark:to-slate-600 rounded-2xl"
          >
            <Card
              className="bg-secondary h-full relative flex flex-col justify-center items-center rounded-2xl"
            >
              <CardHeader className="mt-8 flex justify-center items-center pb-2">
                <img
                  src={imageUrl}
                  alt={`${name} - ${position}`}
                  className="absolute -top-12 rounded-full w-24 h-24 aspect-square object-cover"
                />
                <CardTitle className="text-center text-xl font-semibold">
                  {name}
                </CardTitle>
                <CardDescription className="text-sm">
                  {position}
                </CardDescription>
              </CardHeader>

              <CardContent className="text-center pb-2">
                <p>{slogan}</p>
              </CardContent>

              <CardFooter>
                {socialNetworks.map(({ name, url }) => (
                  <a
                    key={name}
                    href={url}
                    rel="noreferrer noopener"
                    target="_blank"
                    className={buttonVariants({ variant: "ghost", size: "sm" })}
                  >
                    <span className="sr-only">{name} icon</span>
                    {socialIcon(name)}
                  </a>
                ))}
              </CardFooter>
            </Card>
          </div>
        ))}
      </div>
    </section>
  );
};

import { ChartPie, WalletIcon, Magnet } from "lucide-react";
import { Card, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import Lottie from "lottie-react";
import animationData from "@/assets/lottie/promo.json";

type Content = {
  title: string;
  brand: string;
  serviceList: {
    title: string;
    description: string;
    icon: React.ReactNode;
  }[];
};

const content: Content = {
  title: "Service",
  brand: "Buildy/UI",
    serviceList: [
  {
    title: "Code Collaboration",
    description:
      "Seamlessly collaborate with your team using Buildy/UI's modern tools for sharing and reviewing code.",
    icon: <ChartPie className="w-6 h-6 text-white" />,
  },
  {
    title: "Project Management",
    description:
      "Simplify project tracking and enhance productivity with intuitive management features.",
    icon: <WalletIcon className="w-6 h-6 text-white" />,
  },
  {
    title: "Task Automation",
    description:
      "Automate repetitive tasks and save valuable time using Buildy/UI's flexible components.",
    icon: <Magnet className="w-6 h-6 text-white" />,
  },
]} as const;

type PromoServiceLottieProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const PromoServiceLottie = (props: PromoServiceLottieProps) => {
  const { title, brand, serviceList } = {
    ...content,
    ...props
  };

  return (
    <section className="w-full py-8 lg:py-16">
      <div className="container mx-auto px-4 md:px-6 lg:px-8 grid lg:grid-cols-[1fr,1fr] gap-12 place-items-center">
        <div>
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold">
            <span className="bg-gradient-to-b from-sky-500 to-sky-700 text-transparent bg-clip-text">
              {title}
            </span>
            {brand}
          </h2>

          <p className="md:text-lg text-muted-foreground mt-4 mb-8">
            Discover how Buildy/UI can transform your design process with tools
            tailored for efficiency and collaboration.
          </p>

          <div className="flex flex-col gap-6">
            {serviceList?.map(({ icon, title, description }) => (
              <Card key={title} className="flex flex-col relative p-[1px] bg-gradient-to-br from-sky-600 to-sky-300 dark:to-slate-600 rounded-2xl">
                <div className="bg-secondary rounded-2xl">
                <CardHeader className="space-y-1 flex md:flex-row justify-start items-start gap-4">
                  <div className="bg-gradient-to-b from-sky-500 to-sky-700 p-3 rounded-full">{icon}</div>
                  <div>
                    <CardTitle className="font-bold">
                      {title}
                    </CardTitle>
                    <CardDescription className="text-sm mt-2">
                      {description}
                    </CardDescription>
                  </div>
                </CardHeader>
                </div>
              </Card>
            ))}
          </div>
        </div>

        <div className="w-full max-w-md lg:max-w-lg">
          <Lottie animationData={animationData} loop={true} />
        </div>
      </div>
    </section>
  );
};

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { HandHeart, Handshake, HeartHandshake, HandCoins } from "lucide-react";

type Content = {
  title: string;
  promotitle: string;
  description: string;
  features: {
    id: string;
    icon: React.ReactNode;
    title: string;
    description: string;
  }[];
};

const content: Content = {
  title: "How It",
  promotitle: "Works",
  description:
    "Discover how Buildy/UI simplifies UI development step-by-step with modern tools and minimalistic components.",
  features: [
    {
      id: "accessibility",
      icon: <HandHeart className="w-16 h-16 stroke-1 text-sky-700" />, // Custom size and color
      title: "Accessibility",
      description:
        "Ensure inclusive and user-friendly components accessible to all users.",
    },
    {
      id: "community",
      icon: <HeartHandshake className="w-16 h-16 stroke-1 text-sky-700" />, // Custom size and color
      title: "Community",
      description:
        "Leverage an active developer community contributing to continuous improvement.",
    },
    {
      id: "scalability",
      icon: <Handshake className="w-16 h-16 stroke-1 text-sky-700" />, // Custom size and color
      title: "Scalability",
      description:
        "Scale effortlessly with modular, reusable components built for growth.",
    },
    {
      id: "design",
      icon: <HandCoins className="w-16 h-16 stroke-1 text-sky-700" />, // Custom size and color
      title: "Consistency",
      description:
        "Achieve a cohesive design system tailored to your branding needs.",
    },
  ],
} as const;

type PromoHowItWorksProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const PromoHowItWorks = (props: PromoHowItWorksProps) => {
  const { title, promotitle, description, features } = {
    ...content,
    ...props
  };

  return (
  <section className="w-full py-16 lg:py-32">
    <div className="container mx-auto px-4 md:px-6 lg:px-8 text-center">
      <header className="space-y-4">
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold">
          {title}{" "}
          <span className="bg-gradient-to-b from-sky-500 to-sky-700 text-transparent bg-clip-text">
          {promotitle}
          </span>
        </h2>
        <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
          {description}
        </p>
      </header>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 mt-8">
        {features?.map(({ id, icon, title, description }) => (
          <Card key={id} className="relative p-[1px] bg-gradient-to-br from-sky-600 to-sky-300 dark:to-slate-600 rounded-2xl">
            <div className="bg-secondary rounded-2xl h-full">
            <CardHeader>
              <CardTitle className="grid gap-4 place-items-center">
                {icon}
                <span className="text-xl font-semibold">{title}</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-sm text-muted-foreground text-center">
                {description}
              </p>
            </CardContent>
            </div>
          </Card>
        ))}
      </div>
    </div>
  </section>
  );
};

import { Badge } from "@/components/ui/badge";
import {
  Card,
  CardContent,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import responsiveImage from "@/assets/img/strategy.svg";
import intuitiveImage from "@/assets/img/strategy.svg";
import aiImage from "@/assets/img/strategy.svg";

type Content = {
  title: string;
  promotitle: string;
  description: string;
  features: {
    title: string;
    description: string;
    image: string;
  }[];
  featureList: string[];
};

const content: Content = {
  promotitle: "Powerful",
  title: "Features",
  description:
    "Explore the core features of Buildy/UI designed to accelerate your development process and deliver seamless user experiences.",
  features: [
    {
      title: "Responsive Design",
      description:
        "Create interfaces that adapt perfectly to any device with minimal effort.",
      image: responsiveImage,
    },
    {
      title: "Intuitive User Interface",
      description:
        "Build clean, accessible, and intuitive interfaces with pre-designed components.",
      image: intuitiveImage,
    },
    {
      title: "AI-Powered Insights",
      description:
        "Enhance your workflow with intelligent suggestions and prebuilt patterns.",
      image: aiImage,
    },
  ] as Content['features'],
  featureList: [
    "Dark/Light Theme",
    "Reviews",
    "Feature Highlights",
    "Pricing",
    "Contact Form",
    "Our Team",
    "Responsive Design",
    "Newsletter",
    "Minimalist Design",
  ],
} as const;

type PromoFeatureListProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const PromoFeatureList = (props: PromoFeatureListProps) => {
  const { promotitle, title, description, features, featureList } = {
    ...content,
    ...props
  };

  return (
    <section className="container mx-auto px-4 md:px-6 lg:px-8 py-16 lg:py-32 space-y-8">
      <header className="flex flex-col items-center gap-4 text-center">
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold">
          <span className="bg-gradient-to-b from-sky-500 to-sky-700 text-transparent bg-clip-text">
          {promotitle}{" "}
          </span>
          {title}
        </h2>
        <p className="text-lg text-muted-foreground max-w-2xl">
          {description}
        </p>
      </header>

      <div className="flex flex-wrap justify-center gap-4">
        {featureList?.map((feature) => (
          <Badge key={feature} variant="secondary" className="text-sm">
            {feature}
          </Badge>
        ))}
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        {features?.map(({ title, description, image }) => (
          <div
            key={title}
            className="relative p-[1px] bg-gradient-to-br from-sky-600 to-sky-300 dark:to-slate-600 rounded-2xl"
          >
          <Card className="flex flex-col bg-secondary h-full rounded-2xl">
            <CardHeader>
              <CardTitle className="text-xl font-semibold">{title}</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-muted-foreground text-sm">{description}</p>
            </CardContent>
            <CardFooter className="pt-4">
              <img
                src={image}
                alt={title}
                className="w-[150px] lg:w-[250px] mx-auto"
              />
            </CardFooter>
          </Card>
          </div>
        ))}
      </div>
    </section>
  );
};

import wireframe from "@/assets/img/wireframe.svg";

type Content = {
  brand: string;
  title: string;
  description: string;
  imageAlt: string;
  statistics: {
    quantity: string;
    description: string;
  }[];
};

const content: Content = {
  title: "About",
  brand: "Buildy/UI",
  description:
    "Buildy/UI empowers developers and designers to create scalable, flexible, and modern interfaces effortlessly. Designed with Radix and shadcn/ui, it simplifies prototyping and production-ready development for SaaS, marketing, and business applications.",
  imageAlt: "Wireframe showcase",
  statistics: [
    {
      quantity: "10K+",
      description: "Theme Colors",
    },
    {
      quantity: "3K+",
      description: "UI/UX Variants",
    },
    {
      quantity: "50+",
      description: "Purity Components",
    },
    {
      quantity: "8",
      description: "Pretty Templates",
    },
  ] as Content['statistics'],
} as const;

type PromoAboutSectionProps = React.ComponentPropsWithoutRef<"section"> & Partial<Content>;

export const PromoAboutSection = (props: PromoAboutSectionProps) => {
  const { brand, title, description, imageAlt, statistics } = {
    ...content,
    ...props
  };

  return (
    <section className="w-full py-8 lg:py-16">
      <div className="container mx-auto px-4 md:px-6 lg:px-8">
      <div className="relative p-[1px] bg-gradient-to-br from-sky-600 to-sky-300 dark:to-slate-600 rounded-2xl">
        <div className="bg-secondary border rounded-2xl py-12">
            <div className="px-6 flex flex-col-reverse md:flex-row gap-8 md:gap-12">
            <img
                src={wireframe}
                alt={imageAlt}
                className="w-[300px] object-contain"
            />
            <div className="flex flex-col justify-between">
                <div className="pb-6">
                <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold">
                    <span className="bg-gradient-to-b from-sky-500 to-sky-700 text-transparent bg-clip-text">
                    {title}
                    </span>{" "}
                    {brand}
                </h2>
                <p className="text-lg text-muted-foreground mt-4">
                    {description}
                </p>
                </div>
                <Statistics statistics={statistics} />
            </div>
            </div>
            </div>
        </div>
      </div>
    </section>
  );
};

export const Statistics = (props: { statistics: { quantity: string; description: string }[] }) => {
  const { statistics } = props;
  return (
    <section id="statistics">
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-8">
        {statistics?.map(({ quantity, description }) => (
          <div key={description} className="space-y-2 text-center">
            <h2 className="text-3xl sm:text-4xl font-bold">{quantity}</h2>
            <p className="text-lg text-muted-foreground">{description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

navbar.sourceCode:
import * as React from "react";
import { Menu, BookOpen, Layers, Github } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Sheet,
  SheetContent,
  SheetTrigger,
  SheetTitle,
  SheetDescription,
} from "@/components/ui/sheet";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import {
  NavigationMenu,
  NavigationMenuContent,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  NavigationMenuTrigger,
  navigationMenuTriggerStyle,
} from "@/components/ui/navigation-menu";
import { registerViewScript } from '@/utils/viewScript';

type Content = {
  brand: {
    name: string;
    icon: React.ReactNode;
  };
  navigation: {
    main: {
      id: string;
      label: string;
      subItems: {
        id: string;
        title: string;
        description: string;
        href: string;
      }[];
    }[];
    static: {
      id: string;
      path: string;
      label: string;
      icon: React.ReactNode;
    }[];
  };
  actions: {
    id: string;
    path: string;
    label: string;
    icon: React.ReactNode;
  }[];
};

const content: Content = {
  brand: {
    name: "Buildy/UI",
    icon: <Layers className="h-5 w-5" />,
  },
  navigation: {
    main: [
      {
        id: "getting-started",
        label: "Getting Started",
        subItems: [
          {
            id: "intro",
            title: "Introduction",
            description:
              "Re-usable components built using Radix UI and Tailwind CSS",
            href: "#",
          },
          {
            id: "install",
            title: "Installation",
            description:
              "How to install dependencies and structure your app",
            href: "#",
          },
        ],
      },
      {
        id: "components",
        label: "Components",
        subItems: [
          {
            id: "alert-dialog",
            title: "Alert Dialog",
            description:
              "A modal dialog that interrupts the user with important content",
            href: "#",
          },
          {
            id: "hover-card",
            title: "Hover Card",
            description: "Preview content available behind a link",
            href: "#",
          },
        ],
      },
    ],
    static: [
      {
        id: "docs",
        path: "#",
        label: "Documentation",
