          <p>{copyright}</p>
          <ul className="flex gap-4">
            {links?.map((item, Idx) => (
              <li key={`${item.name}-${Idx}`} className="underline hover:text-primary">
                <a href={item.href}>{item.name}</a>
              </li>
            ))}
          </ul>
        </div>
    </div>
  </footer>
  );
};

